mapper.select:

SELECT /* RetrieveScheduleDetail */
       SCH_ID                    AS "scheduleID"
     , "day"                     AS "scheduleDay"
     , SEQNO                     AS "seqNo"
  FROM FRST.SCHED_DETAIL 
 WHERE SCH_ID = #{scheduleID}
   AND "day" = #{scheduleDay}
   AND SEQNO = #{seqNo}
 LIMIT 1
