mapper.select:

SELECT /* RetrieveScheduleInformationDetail */
       SCH_ID                     AS "scheduleID"
     , CLS_INFO_ID                AS "classInfoID"
     , "year"                     AS "scheduleYear"
     , STCD                       AS "statusCode"
  FROM FRST.SCHED_INFO 
 WHERE CLS_INFO_ID = #{classInfoID}
   AND "year" = #{schYear}
   AND SCH_ID = #{scheduleID}
 LIMIT 1
